import * as React from 'react';
import { getGPUTier } from 'detect-gpu';

function useDetectGPU(props) {
  const [GPUTier, setGPUTier] = React.useState(null);
  React.useEffect(() => {
    getGPUTier(props).then(result => setGPUTier(result));
  }, [props]);
  return GPUTier;
}

export { useDetectGPU };
